import React,{useEffect,useState} from 'react'
import Item from './Item'

async function getJson() {
    var jsonData = await fetch('http://localhost:5000/api/items?by=ID')
     .then(response => response.json())
     .then(data => JSON.parse(data))
     return jsonData
   }

  // get the list of JSON items from server by name
   async function getJsonByName() {
    var jsonData = await fetch('http://localhost:5000/api/items?by=name')
     .then(response => response.json())
     .then(data => JSON.parse(data))
     return jsonData
   }

// function get the names of all items in the json object
var nameSetFunc = (jsonRes) => {
    // create a new set
    var nameSet = new Set()

    // loop over json object that is passed in as an argument
    for (const key in jsonRes) {
        if (Object.hasOwnProperty.call(jsonRes, key)) {

            // get each element from object and assign it to const
            const element = jsonRes[key];

            // add the element to a set
            nameSet.add(element['name'])
        }

    // create an array and spread all the set elements into the array
    var nameArray = [...nameSet]

    }return nameArray
}

// react component function
function Itemlist() {

    const [isLoaded,setIsLoaded] = useState(false)
    const [items, setItems] = useState([])
    const [itemsByName, setItemsByName] = useState([])
    const [nameSet, setNameSet] = useState([])

    // run on render
    useEffect(() => {

      // get items from getJson functon and add to 'items' state
      const setJson = async () => {
        var jsonRes = await getJson()
        setItems(jsonRes)

        // pass the JSON data into the 'create set' function
        // and then add it to the Set state
        setNameSet(nameSetFunc(jsonRes))
      }

       // get object of items by name from getJsonByName functon and add to 'itemsByName' state
      const setNamesJson = async () => {
        var jsonRes = await getJsonByName()
        setItemsByName(jsonRes)
      }

      function loading() {
        if (items || itemsByName){
          console.log(items)
        setJson()
        setNamesJson()
        setIsLoaded(true)
        } else {
          loading()
        }
      }

      loading()

    }, [])



  return (
    <div>

      {isLoaded &&
      <div>
        {/* map over each name in the nameSet */}
       {Object.keys(nameSet).map((keyName, i) => (
            <div key={i}>
                <Item name={nameSet[keyName]} items={items} itemsByName={itemsByName}/>
            </div>
        ))}
      </div>
      }

      {(!isLoaded) &&
        <div>loading...</div>
      }

    </div>
  )
}

export default Itemlist
