import React,{useEffect,useState} from 'react'
import Itemchild from './Itemchild'


function Item({name,items,manufacturer,index,setItems}) {

  const [refreshItem,setRefreshItem] = useState(false)
  const [onlyChild,setOnlyChild] = useState(true)

  // get children via queryselector. Index is used as selector.
  const children = document.getElementsByClassName(index)

  function hideChildren(){

    // iterate over children and hide them
    for (const key in children) {
      if (Object.hasOwnProperty.call(children, key)) {
        const element = children[key];
        element.style.display = 'none'
      }
    }
  }

  function toggleHideChildren(){

    // iterate over children and hide them

    if (children[0].style.display === 'none'){
      for (const key in children) {
        if (Object.hasOwnProperty.call(children, key)) {
          const element = children[key];
          element.style.display = ''
        }
      }
    } else if (children[0].style.display === ''){
      for (const key in children) {
        if (Object.hasOwnProperty.call(children, key)) {
          const element = children[key];
          element.style.display = 'none'
        }
      }
    }
  }

  useEffect(
    () => {
      hideChildren()  

      // check if item only has one child.
      if (children.length === 1) {
        setOnlyChild(true)
      }else{
        setOnlyChild(false)
      }
    }
    ,[])

  return (
    <div>

    <div className='item-info-container itemlist-item' onClick={toggleHideChildren} >  
      <div className='item-manufacturer'>{manufacturer}</div>&nbsp;
      <div className='item-name'>{name}</div>
    </div>

    <div className='itemlist-children'>
      {Object.keys(items).map((keyName, i) => {
        if (name === items[keyName]['name']) {
          return <Itemchild 
          itemObject={items[keyName]} 
          index={index} 
          childIndex={keyName} 
          setItems={setItems}
          items={items}
          onlyChild={onlyChild}
          refreshItem={refreshItem}
          setRefreshItem={setRefreshItem}
          key={i}/>
        }
      })}
    </div>

    </div>
  )
}

export default Item
