export function validateBarcode(userInput,state){

    // check if lentgth

    if (userInput.length !== 8){
        state(<span>Length of barcode must be 8 characters</span>)
    }else if (isNaN(userInput)){
        state(<span>Barcode must be a number</span>)
    } else {
        state("")
    }

    return userInput
}


